Vtiger_ListPreview_Js("Accounts_ListPreview_Js",{},{registerHierarchyRecordCount:function(){const iframe=$(".listPreviewframe");iframe.on("load",function(){var contents=iframe.contents();var hierarchyButton=contents.find(".detailViewTitle .hierarchy");if(hierarchyButton){AppConnector.request({module:app.getModuleName(),action:"RelationAjax",record:app.getRecordId(),record:contents.find("#recordId").val(),mode:"getHierarchyCount"}).done(function(response){if(response.success){contents.find(".detailViewTitle .hierarchy .badge").html(response.result)}})}})},postLoadListViewRecordsEvents:function(container){this._super(container);this.registerHierarchyRecordCount()},registerEvents:function(){this._super();this.registerHierarchyRecordCount()}});